use std::ops::ControlFlow;

fn main() {
    let input = "";

    let chars = input.chars();

    let mut buffer = vec![];

    chars.enumerate().try_for_each(|(index, c)| {
        if buffer.len() == 14 {
            let mut buffer_to_check = buffer.clone();
            buffer_to_check.sort();
            buffer_to_check.dedup();
            if buffer_to_check.len() == 14 {
                println!("{}", index);
                return None;
            }
            buffer.remove(0);
        }
        buffer.push(c);
        Some(())
    });
}
